{
  "type": "email",
  "recipient": "recipient@example.com",
  "subject": "Test Communication",
  "content": "This is a test communication message",
  "category": "marketing",
  "priority": "normal",
  "variables": {
    "userName": "John Doe",
    "companyName": "Boosty Platform"
  },
  "templateId": "{{templateId}}"
}

{
  "name": "Standard User Export",
  "description": "Basic user information export template",
  "format": "xlsx",
  "fields": ["name", "email", "phone", "status", "createdAt"],
  "includeRelated": {
    "applications": false,
    "installations": false,
    "communications": false,
    "documents": false
  },
  "filters": {
    "status": "active"
  },
  "sortBy": {
    "field": "createdAt",
    "order": "desc"
  },
  "isDefault": false,
  "isPublic": false
}

{
  "name": "Updated Standard User Export",
  "description": "Updated basic user information export template",
  "fields": ["name", "email", "phone", "status", "createdAt", "updatedAt"],
  "isDefault": true
}

{
  "verificationScore": 95,
  "notes": {
    "verifiedFields": ["name", "documentNumber", "photo"],
    "comments": "Document appears authentic and matches user information"
  }
}

{
  "rejectionReason": "Document expired",
  "notes": {
    "rejectedFields": ["expiryDate"],
    "comments": "Document has expired and needs renewal"
  }
}

{
  "documentIds": ["docId1", "docId2"],
  "rejectionData": {
    "rejectionReason": "Incomplete information",
    "notes": "Bulk rejection due to missing information"
  }
}

{
  "name": "Investor Performance Export",
  "description": "Template for exporting investor performance data",
  "format": "xlsx",
  "columns": ["investorId", "firstName", "lastName", "email", "totalInvestment", "roi", "status"],
  "includeRelatedData": {
    "investments": true,
    "transactions": false,
    "kycDocuments": false
  }
}

{
  "name": "Updated Investor Performance Export",
